from collections import deque
from solution import *


def oracle(graph, start, goal):
    if start == goal:
        return 0
    visited = {start}
    queue = deque([(start, 0)])
    while queue:
        node, dist = queue.popleft()
        for neighbor in graph.get(node, []):
            if neighbor in visited:
                continue
            if neighbor == goal:
                return dist + 1
            visited.add(neighbor)
            queue.append((neighbor, dist + 1))
    return -1


def test_examples():
    graph = {0: [1, 2], 1: [2], 2: [3], 3: []}
    assert bfs_distance(graph, 0, 3) == 2
    assert bfs_distance(graph, 3, 0) == -1
    assert bfs_distance(graph, 1, 1) == 0


def test_randomized():
    random.seed(1010)
    for _ in range(60):
        n = random.randint(2, 15)
        graph = {i: [] for i in range(n)}
        for i in range(n):
            for j in range(i + 1, n):
                if random.random() < 0.2:
                    graph[i].append(j)
                    graph[j].append(i)
        start = random.randrange(n)
        goal = random.randrange(n)
        assert bfs_distance(graph, start, goal) == oracle(graph, start, goal)

