def oracle(values, target):
    best = None
    for i in range(len(values)):
        total = 0
        for j in range(i, len(values)):
            total += values[j]
            if total >= target:
                length = j - i + 1
                if best is None or length < best:
                    best = length
                break
    return best or 0


